import React from 'react';
import './App.css';

class Course113 extends React.Component {

  constructor() {
    super()
    this.state = {
      numeroCliques: 0,
      numeroCliques1: 0,
      numeroCliques2: 0,
    }
    this.handleClick = this.handleClick.bind(this);
    this.handleClick1 = this.handleClick1.bind(this);
    this.handleClick2 = this.handleClick2.bind(this);
  }

  handleClick = () => {
    // console.log(this);
    // console.log('click btn novo');
    this.setState((prevState, _props) => ({
      numeroCliques: prevState.numeroCliques + 1,
    }))
  }
  handleClick1 = () => {
    // console.log(this);
    // console.log('click btn novo');
    this.setState((prevState, _props) => ({
      numeroCliques1: prevState.numeroCliques1 + 1,
    }))
  }
  handleClick2 = () => {
    // console.log(this);
    // console.log('click btn novo');
    this.setState((prevState, _props) => ({
      numeroCliques2: prevState.numeroCliques2 + 1,
    }))
  }

  getButtonColor(num) {
    return num % 2 === 0 ? 'green' : 'white';
  }
  render() {
    // console.log(this);
    return (
      <>
        <div>
        <button onClick={this.handleClick}>Botao 1</button>
        &nbsp;&nbsp;<span>{this.state.numeroCliques}</span>
        &nbsp;&nbsp;<span style={{backgroundColor: this.getButtonColor(this.state.numeroCliques), width: '200px', border: '1px solid black'}}></span>
        </div>
        <div>
        <button onClick={this.handleClick1}>Botao 2</button>
        &nbsp;&nbsp;<span>{this.state.numeroCliques1}</span>
        &nbsp;&nbsp;<span style={{backgroundColor: this.getButtonColor(this.state.numeroCliques1)}}></span>
        </div>
        <div>
        <button onClick={this.handleClick2}>Botao 3</button>
        &nbsp;&nbsp;<span>{this.state.numeroCliques2}</span>
        &nbsp;&nbsp;<span style={{backgroundColor2: this.getButtonColor(this.state.numeroCliques2)}}></span>
        </div>
      </>
    );
  }
};

export default Course113;
