import React from 'react';
import './App.css';

class Course113 extends React.Component {

  constructor() {
    super()
    this.state = {
      numeroCliques: 0,
    }
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick = () => {
    // console.log(this);
    // console.log('click btn novo');
    this.setState((prevState, _props) => ({
      numeroCliques: prevState.numeroCliques + 1,
    }))
  }

  checkEven = () => {
    if (this.state.numeroCliques % 2 === 0) console.log('Verde');
  }
  render() {
    // console.log(this);
    return (
      <>
        <button onClick={this.handleClick}>Botao 1</button>
        &nbsp;&nbsp;<span>{this.state.numeroCliques}</span>
        &nbsp;&nbsp;<span>{this.checkEven}</span>
      </>
    );
  }
};

export default Course113;
